#include<stdio.h>
#include<string.h>
#include<stdlib.h>
#include<math.h>

int longestPalindromeSubseq(char * s){
    
    int i, j, n = strlen(s)+1;
    int d[n][n];
    memset(d,0,sizeof(d));
    for (i = 1; i < n; i++) {
        for (j = n-2; j>=0; j--) {
            if (s[i-1] == s[j]) {
                d[i][j] = d[i-1][j+1] + 1;
            }else{
                d[i][j] = d[i-1][j] > d[i][j+1]?d[i-1][j]:d[i][j+1];
            }
        }
    }
    return d[n-1][0];
}

